    list.forEach((item, key) => {
      if (item[name] === propVal) {
        sameTypeList.push(item)
      } else {
        tempList.push(item)
      }
    })
    resultList.push(sameTypeList)
    list = tempList
    return handleSameTypeList(list, name, resultList)
  } else {
    return resultList
  }
}


