function handleSameTypeList(list, name, resultList){
  // æ¯ä¸€ä¸ªç±»å‹çš„å•ç‹¬æ•°ç»„ï¼Œæ³¨æ„æ­¤å¤„ä¸èƒ½returnå‡ºæ¯ä¸ªsameTypeArrï¼Œ
  // å› ä¸ºé€’å½’çš„è¿”å›å€¼åªè¿”å›æœ€åä¸€æ¬¡çš„å€¼
  let sameTypeList = []
  let propVal = ''

  if (list.length > 0) {
    propVal = list[0][name]
    //log(propVal)
    let tempList = []
    // å°†å«æœ‰ç›¸åŒçš„nameå±æ€§å€¼çš„å¯¹è±¡pushåˆ°æ­¤æ¬¡éå†çš„listä¸­ï¼Œ
    // å°†å…¶ä»–çš„å¯¹è±¡æ”¾å…¥åˆ°tempListä¸­ï¼Œä¸‹æ¬¡éå†
    list.forEach((item, key) => {
      if (item[name] === propVal) {
        sameTypeList.push(item)
      } else {
        tempList.push(item)
      }
    })
    resultList.push(sameTypeList)
    list = tempList
    return handleSameTypeList(list, name, resultList)
  } else {
    return resultList
  }
}


