    checkParams() {
        if (!this.params.url) {
            throw new Error("è¯·å…ˆè°ƒç”¨ .baseUrl(url) ä¼ å…¥é“¾æ¥è¿›è¡Œåˆå§‹åŒ–!")
        }
        if (!this.params.page) {
