                }
                true_url = regObj.exec();
                break;
            }
        }
        if (true_url) {
            return true_url;
        } else {
            return this.params.url
        }
    },
    è·å–å¤„ç†ç»“æœ() {
        return this.exec()
    },
    é“¾æ¥(url) {
